import Taro from '@tarojs/taro'
import { CoverView, CoverImage, View, Image } from '@tarojs/components'
import { useState, useEffect, useCallback } from 'react'

import { shouye0, shouye1, fenlei0, fenlei1, shangjia0, shangjia1, wode0, wode1, xiaoxi0, xiaoxi1, } from "@/constants/images";

import './index.scss'
import { noReadNumber, selectTabIndex } from '@/store/atoms';
import { useRecoilState } from 'recoil';
import { useDidShow } from '@tarojs/runtime';
export default function TabBar() {

  const [noReadNum, setNoReadNum] = useRecoilState(noReadNumber)

  const [selected, setSelected] = useRecoilState(selectTabIndex)
  // const [selected, setSelected] = useState<number>(0)

  const shouye0 = 'data:image/png;base64,'
  const shouye1 = 'data:image/png;base64,'
  const fenlei0 = 'data:image/png;base64,'
  const fenlei1 = 'data:image/png;base64,'
  const xiaoxi0 = 'data:image/png;base64,'
  const xiaoxi1 = 'data:image/png;base64,'
  const wode0 = 'data:image/png;base64,'
  const wode1 = 'data:image/png;base64,'

  let state = {
    selected: 0,
    custom: true,
    color: 'rgba(153, 153, 153, 1)',
    selectedColor: 'rgba(68, 68, 68, 1)',
    backgroundColor: 'white',
    list: [
      {
        pagePath: '/pages/index/index',
        text: '首页',
        path: shouye0,
        checkedPath: shouye1
      },
      {
        pagePath: '/pages/classify/index',
        text: '分类',
        path: fenlei0,
        checkedPath: fenlei1
      },
      {
        pagePath: '/pages/im/index',
        text: '消息',
        path: xiaoxi0,
        checkedPath: xiaoxi1
      },
      {
        pagePath: '/pages/my/index/index',
        text: '我的',
        path: wode0,
        checkedPath: wode1
      }]
  }

  const switchTab = useCallback(
    (item: any, index: any): void => {

      if (selected === index) return

      setSelected(index)

      Taro.switchTab({
        url: item.pagePath
      })
    },
    [selected],
  )

  return (
    <View className='bottom-tab'>
      {
        state.list.map((item, index) => {
          return <View className='bottom-tab-item' onClick={switchTab.bind(this, item, index)} data-path={item.pagePath} key={item.text}>
            <Image className='bottom-tab-item-img' src={selected === index ? item.checkedPath : item.path}></Image>
            <View className='bottom-tab-item-text' style={{ color: selected === index ? state.selectedColor : state.color }}>
              {item.text}
            </View>
            {item.text === '消息' && noReadNum > 0 && <View className='noRead'>{noReadNum}</View>}
          </View>
        })
      }
    </View>

  )
}
